//import dependancies here
import "bulma/css/bulma.css"
import ReactQuill from "react-quill";
import React, { useState } from "react";

function App() {
  const [email, setEmail] = useState("");
  const [user, setUser] = useState(null);
  const [notes, setNotes] = useState([]);
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [date, setDate] = useState

  const onFormSubmit = (event) => {
    event.preventDefault();
    setUser(email);
    //more code here
  };

  const onSaveNote = () => {
    window.alert("saved");
    //more code here
  }

  return (
    <div className="container">
      <div className="columns m-4">
        <div className="columns is-flex is-flex-direction-column ...more classes here...">
          {!user ? (
            //login page
            <>
              <h2 className="title">Login</h2>
              <form onSubmit={(e) => onFormSubmit(e)}>
                <input 
                  className="input my-2"
                  type="email"
                  placeholder="Email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
                <input 
                  type="submit"
                  className="button is-info"
                  value="Login"
                />
              </form>
            </>
          ) : (
            //notes application page
            <>
              <h2 className="subtitle">Welcome, {user}</h2>
              <input 
                className="input" 
                type="text" 
                placeholder="Note Title" 
              />
              <ReactQuill placeholder="Note Body"/>
              <input type="datetime-local" className="input my-2"/>
              <button className="button is-success" onClick={onSaveNote}>Save Note</button>
            </>
          )}

        </div>
      </div>

    </div>

  );
}

export default App;
