//import dependancies here
import ReactQuill from "react-quill";
import React, { useState, useEffect } from "react";
import "bulma/css/bulma.css"
import 'react-quill/dist/quill.snow.css';
import { googleLogout, useGoogleLogin } from '@react-oauth/google';
import axios from 'axios';
import { UNSAFE_DEFERRED_SYMBOL } from "@remix-run/router";

function App() {
  const [email, setEmail] = useState("");
  const [notes, setNotes] = useState([]);
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [date, setDate] = useState("");

  const [ user, setUser ] = useState();
  const [ profile, setProfile ] = useState([]);

  const onSaveNote = () => {
    setNotes([{ title, body, date }, ...notes]);
    //more code here
  }

  //google functions
  const login = useGoogleLogin({
    onSuccess: (codeResponse) => setUser(codeResponse),
    onError: (error) => console.log('Login Failed:', error)
  });

  useEffect(
    () => {

      console.log(profile);
        if (user) {
            axios
                .get(`https://www.googleapis.com/oauth2/v1/userinfo?access_token=${user.access_token}`, {
                    headers: {
                        Authorization: `Bearer ${user.access_token}`,
                        Accept: 'application/json'
                    }
                })
                .then((res) => {
                    setProfile(res.data);
                })
                .catch((err) => console.log(err));
        }
    },
    [ user ]
  );



// log out function to log the user out of google and set the profile array to null
const logOut = () => {
    googleLogout();
    setProfile(null);
};


  return (
    <div className="container">
      <div className="columns m-4">
        <div className="columns is-flex is-flex-direction-column ...more classes here...">      
          {!profile ? (
            //login page
            <>

            <button className="button" onClick={() => login()}>Sign in with Google ðŸš€ </button>


            {/* Placeholder content:
              <h2 className="title">Login</h2>
              <form onSubmit={(e) => onFormSubmit(e)}>
                <input 
                  className="input my-2"
                  type="email"
                  placeholder="Email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
                <input 
                  type="submit"
                  className="button is-info"
                  value="Login"
                />
              </form>
            */}
            </>
          ) : (
            //notes application page
            <>
              <div>
                <img src={profile.picture} alt="user image" />
                <h3>User Logged in</h3>
                <p>Name: {profile.name}</p>
                <p>Email Address: {profile.email}</p>
                <br />
                <br />
                <button onClick={logOut}>Log out</button>
              </div>


              <h2 className="subtitle">Welcome, {profile.name}</h2>
              <input 
                className="input" 
                type="text" 
                placeholder="Note Title" 
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
              <ReactQuill 
                placeholder="Note Body"
                value={body}
                onChange={(e) => setBody(e.target.value)}
                //ERRORS HERE ^^
              />
              <input 
                type="datetime-local" 
                className="input my-2"
                value={date}
                onChange={(e) => setDate(e.target.value)}
              />
              <button className="button is-success" onClick={onSaveNote}>Save Note</button>
            </>
          )}

        </div>
      </div>

    </div>

  );
}

export default App;
